 #设置端口编码
SET NAMES UTF8;
#丢弃数据库，如果存在的话
DROP DATABASE IF EXISTS xh;
#创建数据库
CREATE DATABASE xh CHARSET=UTF8;
#打开数据库
USE xh;
#创建鲜花种类
CREATE TABLE xh_flower_family(
	fid INT PRIMARY KEY AUTO_INCREMENT,
	kname VARCHAR(32)
);
#鲜花种类
#鲜花图片
CREATE TABLE xh_flower_pic(
	pid INT PRIMARY KEY AUTO_INCREMENT,
	flower_id INT,    #鲜花编号
	md VARCHAR(128),  #中图
	lg VARCHAR(128)   #大图
);

#用户信息
CREATE TABLE xh_user(
	uid INT PRIMARY KEY AUTO_INCREMENT,
	uname VARCHAR(32),
	upwd VARCHAR(32),
	email VARCHAR(64),
	phone VARCHAR(11)
);


/****首页轮播广告商品****/
CREATE TABLE xh_index_carousel(
  cid INT PRIMARY KEY AUTO_INCREMENT,
  img VARCHAR(128),
  title VARCHAR(64)
);

 
/****首页商品****/
CREATE TABLE xh_index_product(
  pid INT PRIMARY KEY AUTO_INCREMENT,
  title VARCHAR(64),
  details VARCHAR(128),
  pic VARCHAR(128),
  price DECIMAL(10,2),
  href VARCHAR(128),
  intr VARCHAR(64)
);
#鲜花
CREATE TABLE xh_flower(
	lid INT PRIMARY KEY AUTO_INCREMENT,
	family_id INT,
	title VARCHAR(32),
  fname varchar(16),
	price DECIMAL(6,2),
	market DECIMAL(6,2),
  subhead varchar(32),
	turnover INT,
	comment INT,
  detailed varchar(1024),
	power VARCHAR(1024),
	discuss VARCHAR(1024),
	question VARCHAR(1024),
  prevhref varchar(128),
	prevtitle varchar(32),
	nexthref varchar(128),
	nexttitle varchar(32),
  foreign key(family_id) REFERENCES  xh_flower_family(fid)
);

/**购物车条目**/
CREATE TABLE xh_cart(
  id INT PRIMARY KEY AUTO_INCREMENT,
  uid INT,      #用户编号
  lid INT,   #商品编号
  count INT,        #购买数量
	lname varchar(255),  #商品名称
	price DECIMAL(6,2),  #价格
  img varchar(248) #商品
);